package Java_Basics_1;

public class Char_and_Boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Char Example

		char myChar = 'D';
		char myUnicodeChar = '\u0044'; // From Unicode Website
		System.out.println(myChar);
		System.out.println(myUnicodeChar);
		char myCopyrightChar = '\u00A9'; // From Unicode Website
		System.out.println(myCopyrightChar);

		// Boolean Example
		boolean myTrueBooleanValue = true;
		boolean myFalseBooleanValue = false;

		System.out.println("Boolean True Value = " + myTrueBooleanValue);
		System.out.println("Boolean False Value = " + myFalseBooleanValue);

	}

}
